mapper.select:
SELECT a.*,COUNT(1) as days,IF(b.phone is NULL, 0, 1) AS status from ( SELECT sj,gsd,gsd1,lx,dfxq,xzmc,jzmc,lksj,yys,dz,max(date) as date FROM sjhm WHERE xzdm LIKE concat(#{param.code},'%')
and dfxq = #{param.county}
and xzmc = #{param.township}
and xzmc is null
and date >= #{param.startDate}
and dfxq <= #{param.endDate}
GROUP BY sj,dfxq,xzmc,DATE ) a LEFT JOIN ( SELECT phone FROM troubleshoot_info WHERE phone_status = 0 GROUP BY phone ) b ON a.sj = b.phone
WHERE IF(b.phone IS NULL, 0, 1)=#{param.status}
GROUP BY sj,dfxq,xzmc
HAVING COUNT(1) > #{param.count}

SELECT a.*,COUNT(1) as days,IF(b.phone is NULL, '未排查', '已排查') AS status from ( SELECT sj,gsd,gsd1,lx,dfds,dfxq,jzmc,lksj,yys,jzbs,xzmc,dz,max(date) as date FROM sjhm WHERE xzdm LIKE concat(#{code},'%')
and dfxq = #{county}
and xzmc = #{township}
and xzmc is null
and date >= #{startDate}
and dfxq <= #{endDate}
GROUP BY sj,dfxq,xzmc,DATE ) a LEFT JOIN ( SELECT phone FROM troubleshoot_info WHERE phone_status = 0 GROUP BY phone ) b ON a.sj = b.phone
WHERE IF(b.phone IS NULL, 0, 1)=#{status}
GROUP BY sj,dfxq,xzmc
HAVING COUNT(1) > #{count}

SELECT a.dfxq as area,a.phoneNum,IFNULL(b.whNum,0) AS whNum,IFNULL(c.hbNum,0) AS hbNum, IFNULL(oneNum,0) AS oneNum,IFNULL(twoNum,0) AS twoNum,IFNULL(threeNum,0) AS threeNum FROM ( SELECT a.dfxq,SUM(1) AS phoneNum FROM ( SELECT dfxq,lx FROM sjhm WHERE xzdm LIKE concat(#{code},'%')
and date >= #{startDate}
and date <= #{endDate}
GROUP BY sj,dfxq ) a GROUP BY a.dfxq) a LEFT JOIN ( SELECT a.dfxq,SUM(1) AS whNum FROM ( SELECT dfxq,lx FROM sjhm WHERE xzdm LIKE concat(#{code},'%') AND lx = '武汉'
and date >= #{startDate}
and date <= #{endDate}
GROUP BY sj,dfxq ) a GROUP BY a.dfxq ) b ON a.dfxq = b.dfxq LEFT JOIN ( SELECT a.dfxq,SUM(1) AS hbNum FROM ( SELECT dfxq,lx FROM sjhm WHERE xzdm LIKE concat(#{code},'%') AND lx = '湖北' and sj not in (SELECT sj FROM sjhm WHERE xzdm LIKE concat(#{code},'%') AND lx = '武汉' )
and date >= #{startDate}
and date <= #{endDate}
GROUP BY sj,dfxq ) a GROUP BY a.dfxq) c ON a.dfxq = c.dfxq LEFT JOIN ( SELECT f.name,SUM(1) AS oneNum FROM ( SELECT auth_code,phone FROM troubleshoot_info WHERE auth_code LIKE concat(#{code},'%')
and time >= #{startDate}
and time <= #{endDate}
AND phone_status IN (3,4) GROUP BY phone,auth_code) e JOIN district f ON e.auth_code = f.code) t ON a.dfxq = t.name LEFT JOIN ( SELECT f.name,SUM(1) AS twoNum FROM ( SELECT auth_code,phone FROM troubleshoot_info WHERE auth_code LIKE concat(#{code},'%')
and time >= #{startDate}
and time <= #{endDate}
AND phone_status IN (1,2) GROUP BY phone,auth_code) e JOIN district f ON e.auth_code = f.code) k ON a.dfxq = k.name LEFT JOIN ( SELECT f.name,SUM(1) AS threeNum FROM ( SELECT auth_code,phone FROM troubleshoot_info WHERE auth_code LIKE concat(#{code},'%')
and time >= #{startDate}
and time <= #{endDate}
AND phone_status = 5 GROUP BY phone,auth_code) e JOIN district f ON e.auth_code = f.code) n ON a.dfxq = n.name
